import React, { useState } from 'react';
import './Profile.css';
import profileImage from '../img/profile.jpg'; // Your profile picture
import media1 from '../img/media1.jpg'; // Sample media files
import media2 from '../img/media2.jpg';
import video1 from '../img/video1.mp4';

function Profile() {
  const [isProfileOpen, setProfileOpen] = useState(false);

  const mediaContent = [
    { name: 'Photo 1', type: 'image', src: media1 },
    { name: 'Photo 2', type: 'image', src: media2 },
    { name: 'Video 1', type: 'video', src: video1 }
  ];

  const openProfile = () => {
    setProfileOpen(true);
  };

  const closeProfile = () => {
    setProfileOpen(false);
  };

  return (
    <div className="profile-container">
      <img src={profileImage} alt="Profile" className="profile-image" onClick={openProfile} />
      
      {isProfileOpen && (
        <div className="profile-modal" onClick={closeProfile}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            {mediaContent.map((media, index) => (
              <div className="media-item" key={index}>
                {media.type === 'image' ? (
                  <img src={media.src} alt={media.name} />
                ) : (
                  <video src={media.src} autoPlay loop muted />
                )}
                <p>{media.name}</p>
              </div>
            ))}
            {/* <button onClick={closeProfile}>Close</button> */}
          </div>
        </div>
      )}
    </div>
  );
}

export default Profile;
